set-payload.value:
#[%dw 2.0
output application/json
---
payload map ((item, index) ->
    item mapObject (value, key) -> {
        (p("tablas." ++ vars.integration_id ++ "." ++ vars.tabla ++ "." ++ key) as String default key): value
    }
)]

set-variable.value:
#[var year = now() as String { format: "y"}
var month = now() as String { format: "MM"}
var day = now() as String { format: "dd"}
---
{
	"year": year,
	"month": month,
	"day": day,
	"concatenated": year ++ month ++ day
}]
